ESX = nil

Citizen.CreateThread(
	function()
		while ESX == nil do
			TriggerEvent(
				"esx:getSharedObject",
				function(obj)
					ESX = obj
				end
			)
			Citizen.Wait(0)
		end
	end
)function startAnimAction(lib, anim)	ESX.Streaming.RequestAnimDict(		lib,		function()			TaskPlayAnim(GetPlayerPed(-1), lib, anim, 8.0, 1.0, -1, 49, 0, false, false, false)		end	)end
RegisterNetEvent("ds_doplnky:mainmenu")AddEventHandler(	"ds_doplnky:mainmenu",	function()		OpenAccessoryMenu()	end)
function OpenAccessoryMenu()
	ESX.UI.Menu.Open(
		"default",
		GetCurrentResourceName(),
		"doplnky_menu",
		{
			title = "Accessories",
			align = "bottom-right",
			elements = {
				{label = "Hats", value = "cepice"},
				{label = "Ears", value = "usi"},								{label = "Chains", value = "krk"},
				{label = "Masks", value = "Masks"},
				{label = "Glasses", value = "bryle"},
				{label = "Watches", value = "hodinky",},								{label = "Bracelets", value = "naramky",},								{label = "Bags", value = "tasky",}
			}
		},
		function(data, menu)
			menu.close()
			if data.current.value == "cepice" then				OpenHatsMenu()			elseif data.current.value == "usi" then				OpenEarsMenu()			elseif data.current.value == "Masks" then				OpenMasksMenu()			elseif data.current.value == "bryle" then				OpenGlassesMenu()			elseif data.current.value == "hodinky" then				OpenWatchesMenu()			elseif data.current.value == "naramky" then				OpenBraceletMenu()			elseif data.current.value == "tasky" then				OpenBagsMenu()			elseif data.current.value == "krk" then				OpenChainsMenu()				
			end
		end,
		function(data, menu)			TriggerEvent("ds_interakce:Menu")
			menu.close()
		end
	)
end
function OpenGlassesMenu()	ESX.UI.Menu.Open(		"default",		GetCurrentResourceName(),		"glasses_menu",		{			title = "Glasses",			align = "bottom-right",			elements = {				{label = "Glasses", value = "brylenlist"},								{label = "Take off Glasses", value = "brylesundat"},				{label = "Add Glasses", value = "brylepridat"},				{label = "Remove Glasses", value = "bryleodstranit"}							}		},		function(data, menu)			if data.current.value == "brylenlist" then				ShowChangeBryle()			elseif data.current.value == "brylesundat" then				SundatBryle()			elseif data.current.value == "brylepridat" then				ShowSaveBryle()			elseif data.current.value == "bryleodstranit" then				ShowRemoveBryle()			end		end,		function(data, menu)			OpenAccessoryMenu()			menu.close()		end	)end
function ShowChangeBryle()    ESX.TriggerServerCallback('ds_doplnky:getBryle', function(doplnekbryle)        local options = {}        for k, v in pairs(doplnekbryle) do            v.action = function()                ZmenitBryle(v.skin, v.label)            end            table.insert(options, v)        end        local menu = {            name = 'brylelist',            title = 'Glasses',            options = options        }        TriggerEvent('ds_core:openMenu', menu)    end)endfunction SundatBryle()	startAnimAction("clothingspecs", "try_glasses_positive_a")	Citizen.Wait(1000)	ClearPedTasks(GetPlayerPed(-1))    TriggerEvent('skinchanger:getSkin', function(skin)			local ped = GetPlayerPed(-1)		local prop_glasses = -1		local prop_glasses_text = -1		ClearPedProp(GetPlayerPed(-1), 1)		TriggerServerEvent('ds_doplnky:saveaktualnibryle', prop_glasses, prop_glasses_text)    end)endfunction ZmenitBryle(doplnekbryle, label)    TriggerEvent('skinchanger:getSkin', function(skin)        TriggerEvent('skinchanger:loadDoplnekBryle', skin, doplnekbryle)				local ped = GetPlayerPed(-1)		local prop_glasses = GetPedPropIndex(ped, 1)		local prop_glasses_text = GetPedPropTextureIndex(ped, 1)		TriggerServerEvent('ds_doplnky:saveaktualnibryle', prop_glasses, prop_glasses_text)    end)    ESX.UI.Menu.CloseAll()endfunction ShowSaveBryle()    local menu = {        type = 'dialog',        title = 'Name',        action = SaveBryle    }    TriggerEvent('ds_core:openMenu', menu)endfunction SaveBryle(value)    local ped = GetPlayerPed(-1)	local prop_glasses = GetPedPropIndex(ped, 1)	local prop_glasses_text = GetPedPropTextureIndex(ped, 1)		local skin_data = {["glasses_1"]=prop_glasses,["glasses_2"]=prop_glasses_text}	    TriggerServerEvent('ds_doplnky:saveBryle', value, skin_data)    ESX.UI.Menu.CloseAll()endfunction ShowRemoveBryle()    ESX.TriggerServerCallback('ds_doplnky:getBryle', function(doplnekbryle)        local opti